import React, { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";

const EditModal = ({show, onHide, dataId}) => {
  const [editinput, setEditInput] = useState({
     name : '',
     photo: ''
  })

  
  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Brands</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={""}>
          <Form.Group className="mb-3">
            <Form.Label>Edit brand name</Form.Label>
            <Form.Control value={editinput.name} onChange={(e) => setEditInput(e.target.value)} type="text" />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Change brand logo</Form.Label>
            <Form.Control onChange={""} type="file" />
          </Form.Group>
          <hr />

          <img
            style={{
              width: "100%",
              marginBottom: "10px",
              height: "250px",
              objectFit: "contain",
            }}
            src={''}
            alt=""
          />

          {
            <Button variant="primary" type="submit">
              Edit Brand
            </Button>
          }
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default EditModal;
