// create shop reducers

import { GET_BRANDS_FAILED, GET_BRANDS_REQUEST, GET_BRANDS_SUCCESS } from "../actionTypes.js";
import initialState from "../initialState";

const shopReducers = (state = initialState, {type, payload}) => {
  switch (type) {
    case GET_BRANDS_REQUEST:
        return {
          ...state,
          loading : true
        }
    case GET_BRANDS_SUCCESS:
        return {
          ...state,
          brands : payload,
          loading : false
        }
    case GET_BRANDS_FAILED:
        return {
        
          brands : [],
          loading : false,
          error : payload
        }
    
  
    default:
    return state
  }
}

// export 
export default shopReducers;
