// brand actions

import axios from "axios"
import { GET_BRANDS_REQUEST, GET_BRANDS_SUCCESS } from "./actionTypes"

export const getAllBrand = () => async(dispatch) => {
   dispatch({type : GET_BRANDS_REQUEST})
   await axios.get('http://localhost:5050/api/v1/product/brands').then(res => {
    dispatch({type : GET_BRANDS_SUCCESS, payload : res.data})
   }).catch( err => {
    console.log(err.message)
   })
}
