// brand actions

import axios from "axios";
import {
  GET_BRANDS_FAILED,
  GET_BRANDS_REQUEST,
  GET_BRANDS_SUCCESS,
} from "./actionTypes";

// get all brands
export const getAllBrand = () => async (dispatch) => {
  try {
    dispatch({ type: GET_BRANDS_REQUEST });
    await axios
      .get("http://localhost:5050/api/v1/product/brands")
      .then((res) => {
        dispatch({ type: GET_BRANDS_SUCCESS, payload: res.data });
      })
      .catch((err) => {
        dispatch({ type: GET_BRANDS_FAILED, payload: err.data.message });
      });
  } catch (error) {
    dispatch({ type: GET_BRANDS_FAILED, payload: error.data.message });
  }
};
